// correctAnswer - –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç –¥–ª—è –∫–≤–∏–∑–∞
function createDeck(name, emoji, colorHex, cardsArray, ageRating = null) {
    const cards = cardsArray.map((cardData) => {
        if (Array.isArray(cardData)) {
            const [main, additional, alternatives, isQuiz, correctAnswer] = cardData;
            return new Card(null, main, additional, alternatives || null, isQuiz || false, correctAnswer || null);
        }
        return cardData instanceof Card ? cardData : Card.fromJSON(cardData);
    });
    return new Deck(null, name, emoji, colorHex, cards, true, ageRating);
}

